    {
        Dictionary<String, int> quarterData = new Dictionary<string, int>();
        String thisQuarter;

        foreach (Sales sale in sales)
        {
            thisQuarter = sale.Year.ToString() + " Q" + GetQuarter(sale.Month).ToString();
            if (quarterData.ContainsKey(thisQuarter))
